import React from 'react';
import './Homepage.css';

const Homepage = () => {
  return (
    <div className="homepage">
      <h2>Welcome to the Markdown Article Website</h2>
      <p>
        This is the homepage where you can access various articles written in Markdown format.
        Use the menu on the left to navigate through the articles.
      </p>
      <div className="section">
        <h3>About</h3>
        <p>This website allows you to view articles stored in Markdown format. Simply click on an article from the menu to read more.</p>
      </div>
      <div className="section">
        <h3>How it works</h3>
        <p>Articles are stored as Markdown files in the public folder. Each article is fetched and rendered dynamically when you click on a link from the navigation menu.</p>
      </div>
    </div>
  );
};

export default Homepage;
